    uint256 public tokenCounter;
    event CreatedSVGNFT(uint256 indexed tokenId, string tokenURI);

    mapping(uint256 => string) idToImageURI;

    constructor() ERC721("NFT_StorageOpt", "NFTopt") {
        tokenCounter = 0;
    }

